import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'youtube-ang';
  date = new Date();
  name: string = 'kaustubh'

  constructor() {
    console.log("constructor called");
  }

  doSomething(val: string): void {
    val = "Awesome";
  }
}
